import React from 'react';
import { withRouter } from 'react-router-dom';
import StripeCheckout from 'react-stripe-checkout';
import axios from 'axios';
import { onCheckOutSuccess } from '../../redux/cart/cart-saga';

const StripeButton = ({ price, history }) => {
  const priceForStripe = price * 100;
  const publishableKey = 'pk_test_swgZVNKN4tyqgBXKrWl8R5HW000gFy8jra';

  // const onToken = async (token) => {
  //   try {
  //     await axios({
  //       url: 'payment',
  //       method: 'post',
  //       data: {
  //         amount: priceForStripe,
  //         token,
  //       },
  //     });
  //     alert('Payment successful!');
  //     history.push('/thankyou');
  //     onCheckOutSuccess();
  //   } catch (error) {
  //     console.error('Payment error: ', error);
  //     alert('There was an issue with your payment.');
  //   }
  // };

  const onToken = (token) => {
    axios({
      url: 'payment',
      method: 'post',
      data: {
        amount: priceForStripe,
        token,
      },
    })
      .then((res) => {
        alert('Payment successful');
        checkoutSuccess();
        history.push('/thankyou');
      })
      .catch((error) => {
        console.log('Payment error: ', JSON.parse(error));
        alert(
          'There was an issue with your payment. Please make sure you used the provided test credit card.'
        );
      });
  };

  return (
    <StripeCheckout
      label="Pay Now"
      name="CRSP Clothing Ltd."
      billingAddress
      shippingAddress
      image="https://sendeyo.com/up/d/f3eb2117da"
      description={`Your total is $${price}`}
      amount={priceForStripe}
      panelLabel="Pay Now"
      token={onToken}
      stripeKey={publishableKey}
    />
  );
};

export default withRouter(StripeButton);
